/**
@module ember-cli
*/

const assign = require('ember-cli-lodash-subset').assign;
let logger = require('heimdalljs-logger')('ember-cli:addon-discovery');
const existsSync = require('exists-sync');
const path = require('path');
const resolve = require('resolve');
const findup = require('find-up');
const heimdall = require('heimdalljs');

/**
  AddonDiscovery is responsible for collecting information about all of the
  addons that will be used with a project.

  @class AddonDiscovery
  @extends CoreObject
  @constructor
*/
class AddonDiscovery {
  constructor(ui) {
    this.ui = ui;
  }

  /**
    This is one of the primary APIs for this class and is called by the project.
    It returns a tree of plain objects that each contain information about a
    discovered addon. Each node has `name`, `path`, `pkg` and
    `childAddons` properties. The latter is an array containing any addons
    discovered from applying the discovery process to that addon.

    @private
    @method discoverProjectAddons
   */
  discoverProjectAddons(project) {
    let token = heimdall.start({
      name: `${project.name()}: addon-discovery`,
      addonDiscoveryNode: true,
    });
    let projectAsAddon = this.discoverFromProjectItself(project);
    let internalAddons = this.discoverFromInternalProjectAddons(project);
    let cliAddons = this.discoverFromCli(project.cli);
    let dependencyAddons;

    if (project.hasDependencies()) {
      dependencyAddons = this.discoverFromDependencies(project.root, project.nodeModulesPath, project.pkg, false);
    } else {
      dependencyAddons = [];
    }

    let inRepoAddons = this.discoverInRepoAddons(project.root, project.pkg);
    let addons = projectAsAddon.concat(cliAddons, internalAddons, dependencyAddons, inRepoAddons);

    token.stop();
    return addons;
  }

  /**
    This is one of the primary APIs for this class and is called by addons.
    It returns a tree of plain objects that each contain information about a
    discovered addon. Each node has `name`, `path`, `pkg` and
    `childAddons` properties. The latter is an array containing any addons
    discovered from applying the discovery process to that addon.

    @private
    @method discoverProjectAddons
  */
  discoverChildAddons(addon) {
    let token = heimdall.start({
      name: `${addon.name}: addon-discovery`,
      addonDiscoveryNode: true,
    });
    logger.info('discoverChildAddons: %s(%s)', addon.name, addon.root);
    let dependencyAddons = this.discoverFromDependencies(addon.root, addon.nodeModulesPath, addon.pkg, true);
    let inRepoAddons = this.discoverInRepoAddons(addon.root, addon.pkg);
    let addons = dependencyAddons.concat(inRepoAddons);

    addons = addons.filter(childAddon => !addon.shouldIncludeChildAddon || addon.shouldIncludeChildAddon(childAddon));

    token.stop();
    return addons;
  }

  /**
    Returns an array containing zero or one nodes, depending on whether or not
    the passed project is an addon.

    @private
    @method discoverFromProjectItself
   */
  discoverFromProjectItself(project) {
    if (project.isEmberCLIAddon()) {
      let addonPkg = this.discoverAtPath(project.root);
      if (addonPkg) {
        return [addonPkg];
      }
    }
    return [];
  }

  /**
    Returns a tree based on the addons referenced in the provided `pkg` through
    the package.json `dependencies` and optionally `devDependencies` collections,
    as well as those discovered addons' child addons.

    @private
    @method discoverFromDependencies
   */
  discoverFromDependencies(root, nodeModulesPath, pkg, excludeDevDeps) {
    let discovery = this;
    let addons = Object.keys(this.dependencies(pkg, excludeDevDeps)).map(function(name) {
      if (name !== 'ember-cli') {
        let addonPath = this.resolvePackage(root, name);

        if (addonPath) {
          return discovery.discoverAtPath(addonPath);
        }

        // this supports packages that do not have a valid entry point
        // script (aka `main` entry in `package.json` or `index.js`)
        addonPath = path.join(nodeModulesPath, name);
        let addon = discovery.discoverAtPath(addonPath);
        if (addon) {
          const chalk = require('chalk');

          discovery.ui.writeLine(chalk.yellow(`The package \`${name}\` is not a properly formatted package, we have used a fallback lookup to resolve it at \`${addonPath}\`. This is generally caused by an addon not having a \`main\` entry point (or \`index.js\`).`), 'WARNING');

          return addon;
        }
      }
    }, this).filter(Boolean);
    return addons;
  }

  resolvePackage(root, packageName) {
    try {

      let entryModulePath = resolve.sync(packageName, { basedir: root });

      let pkgPath = findup.sync('package.json', { cwd: entryModulePath });
      if (pkgPath) {
        return path.dirname(pkgPath);
      }

    } catch (e) {
      let acceptableError = `Cannot find module '${packageName}' from '${root}'`;
      // pending: https://github.com/substack/node-resolve/pull/80
      let workAroundError = 'Cannot read property \'isFile\' of undefined';

      if (e.message === workAroundError || e.message === acceptableError) {
        return;
      }
      throw e;
    }
  }

  /**
    Returns a tree based on the in-repo addons referenced in the provided `pkg`
    through paths listed in the `ember-addon` entry, as well as those discovered
    addons' child addons.

    @private
    @method discoverInRepoAddons
   */
  discoverInRepoAddons(root, pkg) {
    if (!pkg || !pkg['ember-addon'] || !pkg['ember-addon'].paths) {
      return [];
    }

    return pkg['ember-addon'].paths
      .map(addonPath => this.discoverAtPath(path.join(root, addonPath)))
      .filter(Boolean);
  }

  /**
    Returns a tree based on the internal addons that may be defined within the project.
    It does this by consulting the projects `supportedInternalAddonPaths()` method, which
    is primarily used for middleware addons.

    @private
    @method discoverFromInternalProjectAddons
   */
  discoverFromInternalProjectAddons(project) {
    return project.supportedInternalAddonPaths()
      .map(path => this.discoverAtPath(path))
      .filter(Boolean);
  }

  discoverFromCli(cli) {
    if (!cli) { return []; }

    const cliPkg = require(path.resolve(cli.root, 'package.json'));
    return this.discoverInRepoAddons(cli.root, cliPkg);
  }

  /**
    Given a particular path, return undefined if the path is not an addon, or if it is,
    a node with the info about the addon.

    @private
    @method discoverAtPath
   */
  discoverAtPath(addonPath) {
    let pkgPath = path.join(addonPath, 'package.json');
    logger.info('attemping to add: %s', addonPath);

    if (existsSync(pkgPath)) {
      const addonPkg = require(pkgPath);
      let keywords = addonPkg.keywords || [];
      logger.info(' - module found: %s', addonPkg.name);

      addonPkg['ember-addon'] = addonPkg['ember-addon'] || {};

      if (keywords.indexOf('ember-addon') > -1) {
        logger.info(' - is addon, adding...');
        let addonInfo = {
          name: addonPkg.name,
          path: addonPath,
          pkg: addonPkg,
        };
        return addonInfo;
      } else {
        logger.info(' - no ember-addon keyword, not including.');
      }
    } else {
      logger.info(` - no package.json (looked at ${pkgPath}).`);
    }

    return null;
  }

  /**
    Returns the dependencies from a package.json
    @private
    @method dependencies
    @param  {Object}  pkg            Package object. If false, the current package is used.
    @param  {Boolean} excludeDevDeps Whether or not development dependencies should be excluded, defaults to false.
    @return {Object}                 Dependencies
   */
  dependencies(pkg, excludeDevDeps) {
    pkg = pkg || {};

    let devDependencies = pkg['devDependencies'];
    if (excludeDevDeps) {
      devDependencies = {};
    }

    return assign({}, devDependencies, pkg['dependencies']);
  }

  addonPackages(addonsList) {
    let addonPackages = {};

    addonsList.forEach(addonPkg => {
      addonPackages[addonPkg.name] = addonPkg;
    });

    return addonPackages;
  }
}

module.exports = AddonDiscovery;

'use strict';

/**
@module ember-cli
*/

const DAG = require('../utilities/DAG');
let logger = require('heimdalljs-logger')('ember-cli:addons-factory');
const heimdall = require('heimdalljs');

/**
  AddonsFactory is responsible for instantiating a collection of addons, in the right order.

  @class AddonsFactory
  @extends CoreObject
  @constructor
*/
class AddonsFactory {
  constructor(addonParent, project) {
    this.addonParent = addonParent;
    this.project = project;
  }

  initializeAddons(addonPackages) {
    let addonParent = this.addonParent;
    let project = this.project;
    let addonParentName = typeof addonParent.name === 'function' ? addonParent.name() : addonParent.name;

    let initializeAddonsToken = heimdall.start(`${addonParentName}: initializeAddons`);
    let graph = new DAG();
    const Addon = require('../models/addon');
    let addonInfo, emberAddonConfig;

    logger.info('initializeAddons for: ', addonParentName);
    logger.info('     addon names are:', Object.keys(addonPackages));

    for (let name in addonPackages) {
      addonInfo = addonPackages[name];
      emberAddonConfig = addonInfo.pkg['ember-addon'];

      graph.addEdges(name, addonInfo, emberAddonConfig.before, emberAddonConfig.after);
    }

    let addons = [];
    graph.topsort(vertex => {
      let addonInfo = vertex.value;
      if (addonInfo) {
        let initializeAddonToken = heimdall.start({
          name: `initialize ${addonInfo.name}`,
          addonName: addonInfo.name,
          addonInitializationNode: true,
        });
        let start = Date.now();
        let AddonConstructor = Addon.lookup(addonInfo);
        let addon = new AddonConstructor(addonParent, project);
        if (addon.initializeAddons) {
          addon.initializeAddons();
        } else {
          addon.addons = [];
        }
        AddonConstructor._meta_.initializeIn = Date.now() - start;
        addon.constructor = AddonConstructor;
        initializeAddonToken.stop();
        addons.push(addon);
      }
    });

    logger.info(' addon info %o', addons.map(addon => ({
      name: addon.name,
      times: {
        initialize: addon.constructor._meta_.initializeIn,
        lookup: addon.constructor._meta_.lookupIn,
      },
    })));

    initializeAddonsToken.stop();

    return addons;
  }
}

module.exports = AddonsFactory;

'use strict';

function visit(vertex, fn, visited, path) {
  let name = vertex.name,
      vertices = vertex.incoming,
      names = vertex.incomingNames,
      len = names.length,
      i;
  if (!visited) {
    visited = {};
  }
  if (!path) {
    path = [];
  }
  if (visited.hasOwnProperty(name)) {
    return;
  }
  path.push(name);
  visited[name] = true;
  for (i = 0; i < len; i++) {
    visit(vertices[names[i]], fn, visited, path);
  }
  fn(vertex, path);
  path.pop();
}

class DAG {
  /**
   * Directed acyclic graph.
   *
   * see [Wikipedia](https://en.wikipedia.org/wiki/Directed_acyclic_graph).
   *
   * @class DAG
   * @constructor
   */
  constructor() {
    /**
     * Array of all known vertex names.
     *
     * @final
     * @property names
     * @type Array
     */
    this.names = [];

    /**
     * Hash of vertices keyed by name.
     *
     * @final
     * @property vertices
     * @type Object
     */
    this.vertices = {};
  }

  /**
   * Adds a new vertex to the graph.
   *
   * @method add
   * @param {String} name Name of the vertex
   * @return {Object|undefined} The new vertex or the already existing vertex with the same name
   */
  add(name) {
    if (!name) { return; }
    if (this.vertices.hasOwnProperty(name)) {
      return this.vertices[name];
    }
    let vertex = {
      name,
      incoming: {},
      incomingNames: [],
      hasOutgoing: false,
      value: null,
    };

    this.vertices[name] = vertex;
    this.names.push(name);
    return vertex;
  }

  /**
   * Assigns `value` to the vertex named `name`.
   *
   * Creates a new vertex if a vertex named `name` does not exist yet.
   *
   * @method map
   * @param {String} name Name of the vertex
   * @param {any} value Value of the vertex
   */
  map(name, value) {
    this.add(name).value = value;
  }

  /**
   * Adds an edge between two vertices.
   *
   * @method addEdge
   * @param fromName Name of the `from` vertex
   * @param toName Name of the `to` vertex
   * @throws {Error} if a cycle is detected
   */
  addEdge(fromName, toName) {
    if (!fromName || !toName || fromName === toName) {
      return;
    }
    let from = this.add(fromName), to = this.add(toName);
    if (to.incoming.hasOwnProperty(fromName)) {
      return;
    }
    function checkCycle(vertex, path) {
      if (vertex.name === toName) {
        throw new Error(`cycle detected: ${toName} <- ${path.join(' <- ')}`);
      }
    }
    visit(from, checkCycle);
    from.hasOutgoing = true;
    to.incoming[fromName] = from;
    to.incomingNames.push(fromName);
  }

  /**
   * Traverses the graph depth-first.
   *
   * Each vertex will only be visited once even if it has multiple parent vertices.
   *
   * @method topsort
   * @param {Function} fn Function that is called with each vertex and the path to it
   */
  topsort(fn) {
    let visited = {},
        vertices = this.vertices,
        names = this.names,
        len = names.length,
        i, vertex;
    for (i = 0; i < len; i++) {
      vertex = vertices[names[i]];
      if (!vertex.hasOutgoing) {
        visit(vertex, fn, visited);
      }
    }
  }

  /**
   * Assigns `value` to the vertex named `name` and creates the edges defined by `before` and `after`.
   *
   * @method addEdges
   * @param {String} name Name of the vertex
   * @param {any} value Value of the vertex
   * @param {String|Array} before Adds edges from `name` to each of `before`
   * @param {String|Array} after Adds edges from each of `after` to `name`
   */
  addEdges(name, value, before, after) {
    let i;
    this.map(name, value);
    if (before) {
      if (typeof before === 'string') {
        this.addEdge(name, before);
      } else {
        for (i = 0; i < before.length; i++) {
          this.addEdge(name, before[i]);
        }
      }
    }
    if (after) {
      if (typeof after === 'string') {
        this.addEdge(after, name);
      } else {
        for (i = 0; i < after.length; i++) {
          this.addEdge(after[i], name);
        }
      }
    }
  }
}

module.exports = DAG;

'use strict';
const exit = require('capture-exit');
exit.captureExit();

const fs = require('fs-extra');
const existsSync = require('exists-sync');
const path = require('path');
const Promise = require('rsvp').Promise;
const CoreObject = require('core-object');
const SilentError = require('silent-error');
const chalk = require('chalk');
const attemptNeverIndex = require('../utilities/attempt-never-index');
const findBuildFile = require('../utilities/find-build-file');
const _resetTreeCache = require('./addon')._resetTreeCache;

const Sync = require('tree-sync');
const heimdall = require('heimdalljs');

/**
 * Wrapper for the Broccoli [Builder](https://github.com/broccolijs/broccoli/blob/master/lib/builder.js) class.
 *
 * @private
 * @module ember-cli
 * @class Builder
 * @constructor
 * @extends Task
 */
class Builder extends CoreObject {

  constructor(options) {
    super(options);

    this.setupBroccoliBuilder();
    this.trapSignals();
    this._instantiationStack = (new Error()).stack.replace(/[^\n]*\n/, '');
  }

  /**
   * @private
   * @method setupBroccoliBuilder
   */
  setupBroccoliBuilder() {
    this.environment = this.environment || 'development';
    process.env.EMBER_ENV = process.env.EMBER_ENV || this.environment;

    const broccoli = require('broccoli-builder');
    let hasBrocfile = existsSync(path.join('.', 'Brocfile.js'));
    let buildFile = findBuildFile('ember-cli-build.js');

    if (hasBrocfile) {
      this.ui.writeDeprecateLine('Brocfile.js has been deprecated in favor of ember-cli-build.js. Please see the transition guide: https://github.com/ember-cli/ember-cli/blob/master/TRANSITION.md#user-content-brocfile-transition.');
      this.tree = require('broccoli-brocfile-loader')();
    } else if (buildFile) {
      this.tree = buildFile({ project: this.project });
    } else {
      throw new Error('No ember-cli-build.js found. Please see the transition guide: https://github.com/ember-cli/ember-cli/blob/master/TRANSITION.md#user-content-brocfile-transition.');
    }

    this.builder = new broccoli.Builder(this.tree);
  }

  /**
   * @private
   * @method trapSignals
   */
  trapSignals() {
    this._boundOnSIGINT = this.onSIGINT.bind(this);
    this._boundOnSIGTERM = this.onSIGTERM.bind(this);
    this._boundOnMessage = this.onMessage.bind(this);
    this._boundCleanup = this.cleanup.bind(this);

    process.on('SIGINT', this._boundOnSIGINT);
    process.on('SIGTERM', this._boundOnSIGTERM);
    process.on('message', this._boundOnMessage);
    exit.onExit(this._boundCleanup);

    if (/^win/.test(process.platform)) {
      this.trapWindowsSignals();
    }
  }

  _cleanupSignals() {
    process.removeListener('SIGINT', this._boundOnSIGINT);
    process.removeListener('SIGTERM', this._boundOnSIGTERM);
    process.removeListener('message', this._boundOnMessage);
    exit.offExit(this._boundCleanup);

    if (/^win/.test(process.platform)) {
      this._cleanupWindowsSignals();
    }
  }

  /**
   * @private
   * @method trapWindowsSignals
   */
  trapWindowsSignals() {
    // This is required to capture Ctrl + C on Windows
    if (process.stdin && process.stdin.isTTY) {
      process.stdin.setRawMode(true);
      this._windowsCtrlCTrap = function(data) {
        if (data.length === 1 && data[0] === 0x03) {
          process.emit('SIGINT');
        }
      };
      process.stdin.on('data', this._windowsCtrlCTrap);
    }
  }

  _cleanupWindowsSignals() {
    if (this._windowsCtrlCTrap && process.stdin.removeListener) {
      process.stdin.removeListener('data', this._windowsCtrlCTrap);
    }
  }

  /**
    Determine whether the output path is safe to delete. If the outputPath
    appears anywhere in the parents of the project root, the build would
    delete the project directory. In this case return `false`, otherwise
    return `true`.

    @private
    @method canDeleteOutputPath
    @param {String} outputPath
    @return {Boolean}
  */
  canDeleteOutputPath(outputPath) {
    let rootPathParents = [this.project.root];
    let dir = path.dirname(this.project.root);
    rootPathParents.push(dir);
    while (dir !== path.dirname(dir)) {
      dir = path.dirname(dir);
      rootPathParents.push(dir);
    }
    return rootPathParents.indexOf(outputPath) === -1;
  }

  /**
   * @private
   * @method copyToOutputPath
   * @param {String} inputPath
   */
  copyToOutputPath(inputPath) {
    let outputPath = this.outputPath;

    fs.mkdirsSync(outputPath);

    if (!this.canDeleteOutputPath(outputPath)) {
      throw new SilentError(`Using a build destination path of \`${outputPath}\` is not supported.`);
    }

    let sync = this._sync;
    if (sync === undefined) {
      this._sync = sync = new Sync(inputPath, path.resolve(this.outputPath));
    }

    let changes = sync.sync();

    return changes.map(op => op[1]);
  }

  /**
   * @private
   * @method processBuildResult
   * @param results
   * @return {Promise}
   */
  processBuildResult(results) {
    let self = this;

    return Promise.resolve()
      .then(() => self.copyToOutputPath(results.directory))
      .then(syncResult => {
        results.outputChanges = syncResult;
        return results;
      });
  }

  /**
   * @private
   * @method processAddonBuildSteps
   * @param buildStep
   * @param results
   * @return {Promise}
   */
  processAddonBuildSteps(buildStep, results) {
    let addonPromises = [];
    if (this.project && this.project.addons.length) {
      addonPromises = this.project.addons.map(addon => {
        if (addon[buildStep]) {
          return addon[buildStep](results);
        }
      }).filter(Boolean);
    }

    return Promise.all(addonPromises).then(() => results);
  }

  /**
   * @private
   * @method build
   * @return {Promise}
   */
  build(willReadStringDir, resultAnnotation) {
    this.project._instrumentation.start('build');

    let self = this;
    attemptNeverIndex('tmp');

    return this.processAddonBuildSteps('preBuild')
      .then(() => self.builder.build(willReadStringDir))
      .then(this.processAddonBuildSteps.bind(this, 'postBuild'))
      .then(this.processBuildResult.bind(this))
      .then(this.processAddonBuildSteps.bind(this, 'outputReady'))
      .then(result => {
        self.project._instrumentation.stopAndReport('build', result, resultAnnotation);
        return result;
      }, reason => {
        self.project._instrumentation.stopAndReport('build', null, resultAnnotation);
        throw reason;
      })
      .then(this.checkForPostBuildEnvironmentIssues.bind(this))
      .catch(error => {
        this.processAddonBuildSteps('buildError', error);
        throw error;
      });
  }

  /**
   * Delegates to the `cleanup` method of the wrapped Broccoli builder.
   *
   * @private
   * @method cleanup
   * @return {Promise}
   */
  cleanup() {
    let ui = this.project.ui;
    ui.startProgress('cleaning up');
    ui.writeLine('cleaning up...');

    // ensure any addon treeFor caches are reset
    _resetTreeCache();

    this._cleanupSignals();

    let node = heimdall.start({ name: 'Builder Cleanup' });

    return this.builder.cleanup().finally(() => {
      ui.stopProgress();
      node.stop();
    }).catch(err => {
      ui.writeLine(chalk.red('Cleanup error.'));
      ui.writeError(err);
    });
  }

  /**
   * Checks for issues in the environment that can't easily be detected until
   * after a build and issues any necessary deprecation warnings.
   *
   * - check for old (pre 0.1.4) versions of heimdalljs
   *
   * @private
   * @method checkForPostBuildEnvironmentIssues
   */
  checkForPostBuildEnvironmentIssues(value) {
    // 0.1.3 and prior used a global heimdall instance to share sessions
    // newer versions keep the session itself on process
    this.project.ui.writeDeprecateLine('Heimdalljs < 0.1.4 found.  Please remove old versions of heimdalljs and reinstall (you can find them with `npm ls heimdalljs` as long as you have nothing `npm link`d).  Performance instrumentation data will be incomplete until then.', !process._heimdall);

    return value;
  }

  /**
   * Handles the `SIGINT` signal.
   *
   * Calls {{#crossLink "Builder/cleanupAndExit:method"}}{{/crossLink}} by default.
   *
   * @private
   * @method onSIGINT
   */
  onSIGINT() {
    process.exit(1);
  }

  /**
   * Handles the `SIGTERM` signal.
   *
   * Calls {{#crossLink "Builder/cleanupAndExit:method"}}{{/crossLink}} by default.
   *
   * @private
   * @method onSIGTERM
   */
  onSIGTERM() {
    process.exit(1);
  }

  /**
   * Handles the `message` event on the `process`.
   *
   * Calls `process.exit` if the `kill` property on the `message` is set.
   *
   * @private
   * @method onMessage
   */
  onMessage(message) {
    if (message.kill) {
      process.exit(1);
    }
  }
}

module.exports = Builder;

