function methodsForTreeType(treeType) {
  let treeMethods = DEFAULT_TREE_FOR_METHOD_METHODS[treeType];

  return GLOBAL_TREE_FOR_METHOD_METHODS.concat(treeMethods);
}

/*
  `cacheKeyForTree` is currently hidden behind an experiment (pending review
  and landing of https://github.com/ember-cli/rfcs/pull/90).

  Once that RFC is accepted, a few things need to happen:

  * Remove `ADDON_TREE_CACHING` from `lib/experiments/index.js`.
  * Remove this guard.
  * Remove guard around tests in `tests/unit/models/addon-test.js`.
  * Move this code back up into the `addonProto` directly

*/
let addonTreeCacheExperimentPresent = !!experiments.ADDON_TREE_CACHING;
if (addonTreeCacheExperimentPresent) {
  addonProto[experiments.ADDON_TREE_CACHING] = function cacheKeyForTree(treeType) {
    let self = this;
    let methodsToValidate = methodsForTreeType(treeType);
    let cacheKeyStats = heimdall.statsFor('cache-key-for-tree');

    // determine if treeFor* (or other methods for tree type) overrides for the given tree
    let modifiedMethods = methodsToValidate.filter(methodName => self[methodName] !== addonProto[methodName]);

    if (modifiedMethods.length) {
      cacheKeyStats.modifiedMethods++;
      cacheKeyLogger.info(`Opting out due to: modified methods: ${modifiedMethods.join(', ')}`);
      return null; // uncacheable
    }

    // determine if treeForMethods overrides for given tree
    if (self.treeForMethods[treeType] !== DEFAULT_TREE_FOR_METHODS[treeType]) {
      cacheKeyStats.treeForMethodsOverride++;
      cacheKeyLogger.info('Opting out due to: treeForMethods override');
      return null; // uncacheable
    }

    // compute cache key
    let cacheKey = calculateCacheKeyForTree(treeType, self);

    return cacheKey; // profit?
  };
}

let Addon = CoreObject.extend(addonProto);

/**
  Returns the absolute path for a given addon

  @private
  @method resolvePath
  @param {String} addon Addon name
  @return {String} Absolute addon path
*/
Addon.resolvePath = function(addon) {
  let addonMain = addon.pkg['ember-addon-main'];

  if (addonMain) {
    this.ui && this.ui.writeDeprecateLine(`${addon.pkg.name} is using the deprecated ember-addon-main definition. It should be updated to {'ember-addon': {'main': '${addon.pkg['ember-addon-main']}'}}`);
  } else {
    addonMain = (addon.pkg['ember-addon'] && addon.pkg['ember-addon'].main) || addon.pkg['main'] || 'index.js';
  }

  // Resolve will fail unless it has an extension
  if (!path.extname(addonMain)) {
    addonMain += '.js';
  }

  return path.resolve(addon.path, addonMain);
};

/**
  Returns the addon class for a given addon name.
  If the Addon exports a function, that function is used
  as constructor. If an Object is exported, a subclass of
  `Addon` is returned with the exported hash merged into it.

  @private
  @static
  @method lookup
  @param {String} addon Addon name
  @return {Addon} Addon class
*/
Addon.lookup = function(addon) {
  let Constructor, addonModule, modulePath, moduleDir;
  let start = Date.now();

  modulePath = Addon.resolvePath(addon);
  moduleDir = path.dirname(modulePath);

  if (existsSync(modulePath)) {
    addonModule = require(modulePath);

    if (typeof addonModule === 'function') {
      Constructor = addonModule;
      Constructor.prototype.root = Constructor.prototype.root || moduleDir;
      Constructor.prototype.pkg = Constructor.prototype.pkg || addon.pkg;
    } else {
      Constructor = Addon.extend(assign({
        root: moduleDir,
        pkg: addon.pkg,
      }, addonModule));
    }
  }

  if (!Constructor) {
    throw new SilentError(`The \`${addon.pkg.name}\` addon could not be found at \`${addon.path}\`.`);
  }

  let lookupDuration = Date.now() - start;

  Constructor._meta_ = {
    modulePath,
    lookupDuration,
    initializeIn: 0,
  };

  return Constructor;
};
module.exports = Addon;
module.exports._resetTreeCache = _resetTreeCache;

if (addonTreeCacheExperimentPresent) {
  module.exports._treeCache = ADDON_TREE_CACHE;
}

'use strict';

let logger = require('heimdalljs-logger')('ember-cli:installation-checker');
const fs = require('fs-extra');
const existsSync = require('exists-sync');
const path = require('path');
const SilentError = require('silent-error');

class InstallationChecker {
  constructor(options) {
    this.project = options.project;
  }

  /**
  * Check if npm and bower installation directories are present,
  * and raise an error message with instructions on how to proceed.
  *
  * If some of these package managers aren't being used in the project
  * we just ignore them. Their usage is considered by checking the
  * presence of your manifest files: package.json for npm and bower.json for bower.
  *
  * @method checkInstallations
  */
  checkInstallations() {
    let commands = [];

    if (this.usingNpm() && this.npmDependenciesNotPresent()) {
      logger.info('npm dependencies not installed');
      commands.push('`npm install`');
    }
    if (this.usingBower() && this.bowerDependenciesNotPresent()) {
      logger.info('bower dependencies not installed');
      commands.push('`bower install`');
    }
    if (commands.length) {
      let commandText = commands.join(' and ');
      throw new SilentError(`No dependencies installed. Run ${commandText} to install missing dependencies.`);
    }
  }

  hasBowerDeps() {
    return hasDependencies(readJSON(path.join(this.project.root, 'bower.json')));
  }

  usingBower() {
    return existsSync(path.join(this.project.root, 'bower.json')) && this.hasBowerDeps();
  }

  bowerDependenciesNotPresent() {
    return !existsSync(this.project.bowerDirectory);
  }

  hasNpmDeps() {
    return hasDependencies(readJSON(path.join(this.project.root, 'package.json')));
  }

  usingNpm() {
    return existsSync(path.join(this.project.root, 'package.json')) && this.hasNpmDeps();
  }

  npmDependenciesNotPresent() {
    return !existsSync(this.project.nodeModulesPath);
  }
}

module.exports = InstallationChecker;

function hasDependencies(pkg) {
  return (pkg.dependencies && pkg.dependencies.length) ||
         (pkg.devDependencies && pkg.devDependencies.length);
}

function readJSON(path) {
  try {
    return fs.readJsonSync(path);
  } catch (e) {
    throw new SilentError(`InstallationChecker: Unable to parse: ${path}`);
  }
}

'use strict';

const Watcher = require('./watcher');

class ServerWatcher extends Watcher {
  constructor(options) {
    super(options);

    this.watcher.on('add', this.didAdd.bind(this));
    this.watcher.on('delete', this.didDelete.bind(this));
  }

  constructWatcher(options) {
    return new (require('sane'))(this.watchedDir, options);
  }

  didChange(relativePath) {
    let description = `File changed: "${relativePath}"`;

    this.ui.writeLine(description);
    this.analytics.track({
      name: 'server file changed',
      description,
    });
  }

  didAdd(relativePath) {
    let description = `File added: "${relativePath}"`;

    this.ui.writeLine(description);
    this.analytics.track({
      name: 'server file addition',
      description,
    });
  }

  didDelete(relativePath) {
    let description = `File deleted: "${relativePath}"`;

    this.ui.writeLine(description);
    this.analytics.track({
      name: 'server file deletion',
      description,
    });
  }
}

module.exports = ServerWatcher;

'use strict';

const chalk = require('chalk');
let logger = require('heimdalljs-logger')('ember-cli:watcher');
const CoreObject = require('core-object');

class Watcher extends CoreObject {
  constructor(_options) {
    super(_options);

    this.verbose = true;

    let options = this.buildOptions();

    logger.info('initialize %o', options);

    this.watcher = this.watcher || this.constructWatcher(options);

    this.watcher.on('error', this.didError.bind(this));
    this.watcher.on('change', this.didChange.bind(this));
  }

  constructWatcher(options) {
    return new (require('ember-cli-broccoli-sane-watcher'))(this.builder, options);
  }

  didError(error) {
    logger.info('didError %o', error);
    this.ui.writeError(error);
    this.analytics.trackError({
      description: error && error.message,
    });
  }

  then() {
    return this.watcher.then.apply(this.watcher, arguments);
  }

  didChange(results) {
    logger.info('didChange %o', results);

    let totalTime = results.totalTime / 1e6;

    this.ui.writeLine('');
    this.ui.writeLine(chalk.green(`Build successful - ${Math.round(totalTime)}ms.`));

    this.analytics.track({
      name: 'ember rebuild',
      message: `broccoli rebuild time: ${totalTime}ms`,
    });

    /*
     * We use the `rebuild` category in our analytics setup for both builds
     * and rebuilds. This is a bit confusing, but the actual thing we
     * delineate on in the reports is the `variable` value below. This is
     * used both here and in `lib/tasks/build.js`.
     */
    this.analytics.trackTiming({
      category: 'rebuild',
      variable: 'rebuild time',
      label: 'broccoli rebuild time',
      value: Number(totalTime),
    });
  }

  on() {
    this.watcher.on.apply(this.watcher, arguments);
  }

  off() {
    this.watcher.off.apply(this.watcher, arguments);
  }

  buildOptions() {
    let watcher = this.options && this.options.watcher;

    if (watcher && ['polling', 'watchman', 'node', 'events'].indexOf(watcher) === -1) {
      throw new Error(`Unknown watcher type --watcher=[polling|watchman|node] but was: ${watcher}`);
    }

    return {
      verbose: this.verbose,
      poll: watcher === 'polling',
      watchman: watcher === 'watchman' || watcher === 'events',
      node: watcher === 'node',
    };
  }
}

module.exports = Watcher;

